mapper.select:
SELECT b.board_id, b.category, b.title, b.view_count as viewCount, b.created_at as createdAt, m.name as nickname FROM board b JOIN member m ON b.user_id = m.user_id
AND b.category = #{category}
ORDER BY b.created_at DESC

mapper.update:
UPDATE board
SET title    = #{title},
    content  = #{content},
    category = #{category},
    img_url  = #{imgUrl}
WHERE board_id = #{boardId}

UPDATE board
SET view_count = view_count + 1
WHERE board_id = #{boardId}
