mapper.select:
SELECT *
FROM member
WHERE login_id = #{loginId}
  AND email = #{email}

SELECT COUNT(*)
FROM member
WHERE email = #{email}

SELECT COUNT(*)
FROM member
WHERE login_id = #{loginId}

SELECT COUNT(*)
FROM member
WHERE nickname = #{nickname}

mapper.update:
UPDATE member
name = #{name}, nickname = #{nickname}, birth_year = #{birthYear}, gender = #{gender}, height = #{height}, weight = #{weight}, activity_level = #{activityLevel}, diet_goal = #{dietGoal}, custom_tdee = #{customTdee}, disease_codes = #{diseaseCodes}, allergy_codes = #{allergyCodes}, role = #{role}, updated_at = NOW()
WHERE user_id = #{userId}

UPDATE member
SET last_login_at = NOW()
WHERE user_id = #{userId}

UPDATE member
SET status = 'WITHDRAWN'
WHERE user_id = #{userId}
