mapper.select:
SELECT *
FROM member
WHERE username = #{username}
  AND email = #{email}

SELECT COUNT(*)
FROM member
WHERE email = #{email}

SELECT COUNT(*)
FROM member
WHERE nickname = #{nickname}

SELECT COUNT(*)
FROM member
WHERE username = #{username}

mapper.update:
UPDATE member
nickname = #{nickname}, height = #{height}, weight = #{weight}, activity_level = #{activityLevel}, diet_goal = #{dietGoal}, custom_tdee = #{customTdee}, role = #{role}, status = #{status}, updated_at = NOW()
WHERE user_id = #{userId}

UPDATE member
SET last_login_at = NOW()
WHERE user_id = #{userId}

UPDATE member
SET status = 'WITHDRAWN'
WHERE user_id = #{userId}

mapper.delete:
UPDATE member
SET refresh_token = NULL
WHERE user_id = #{userId}
